create table credentials_entity
(
    id        varchar(36)                                                not null
        primary key,
    name      varchar(128)                                               not null,
    data      text                                                       not null,
    type      varchar(32)                                                not null,
    createdAt datetime(3) default (STRFTIME('%Y-%m-%d %H:%M:%f', 'NOW')) not null,
    updatedAt datetime(3) default (STRFTIME('%Y-%m-%d %H:%M:%f', 'NOW')) not null,
    isManaged BOOLEAN     default 0                                      not null
);

create index idx_credentials_entity_type
    on credentials_entity (type);

